import React, { useState, useEffect } from "react";
import Sidebar from "../reuse/user-side";
import { Link } from "react-router-dom";

const Settings = () => {
  const [userdetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editForm, setEditForm] = useState({
    username: '',
    email: '',
    location: '',
    password: '',
    confirmPassword: '',
  });
  const [currentPassword, setCurrentPassword] = useState(''); // Current password input field
  const [showUpdateForm, setShowUpdateForm] = useState(false); // To control form visibility
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (!token) {
      console.error("No token found, user is not logged in.");
      return;
    }

    fetch('http://127.0.0.1:8000/api/settings/', {
      method: 'GET',
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'application/json',
      },
    })
      .then(response => response.json())
      .then(data => {
        setUserDetails(data);
        setEditForm({
          username: data.username,
          email: data.email,
          location: data.location || '',
          password: '',
          confirmPassword: '', // Reset confirm password
        });
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setLoading(false);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handlePasswordSubmit = (e) => {
    e.preventDefault();

    const token = localStorage.getItem('authToken');
    if (!token) return;

    // Verify current password with backend
    fetch('http://127.0.0.1:8000/api/verify_password/', {
      method: 'POST',
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        current_password: currentPassword,
      }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.success) {
          setShowUpdateForm(true); // If password is correct, show the update form
          setError('');
        } else {
          setError('Current password is incorrect');
          setShowUpdateForm(false); // Hide the update form if password is incorrect
        }
      })
      .catch(error => {
        console.error('Error verifying password:', error);
        setError('An error occurred while verifying your password');
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Password validation
    if (editForm.password !== editForm.confirmPassword) {
      setError("Passwords do not match!");
      return;
    }
    setError(""); // Clear error if passwords match

    const token = localStorage.getItem('authToken');
    if (!token) return;

    fetch('http://127.0.0.1:8000/api/settings/', {
      method: 'PUT',
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username: editForm.username,
        email: editForm.email,
        location: editForm.location,
        password: editForm.password, // Send new password
      }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setError(data.error); // Show error if passwords don't match
          setSuccessMessage(''); // Clear success message
        } else {
          setUserDetails(data);
          setSuccessMessage('Your details were updated successfully!');
          setEditForm({
            username: data.username,
            email: data.email,
            location: data.location || '',
            password: '',
            confirmPassword: '', // Reset confirm password after update
          });
          setError(''); // Clear any errors
        }
      })
      .catch(error => {
        console.error('Error updating user data:', error);
        setError('An error occurred while updating your details.');
        setSuccessMessage(''); // Clear success message
      });
  };

  return (
    <div>
      <div className='head-customer'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">SMARTPLAY</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto"></ul>
            </div>
          </div>
        </nav>
      </div>
      <div className='dashboard-container'>
        <Sidebar />
        <div className='dashboard-content'>
          <h1>Settings</h1>
          <h3>Your Details</h3>
          {loading ? (
            <p>Loading user details...</p>
          ) : userdetails ? (
            <div>
              <p><strong>Username:</strong> {userdetails.username}</p>
              <p><strong>Email:</strong> {userdetails.email}</p>
              <p><strong>Location:</strong> {userdetails.location || "Not provided"}</p>
              <p><strong>User Type:</strong> {userdetails.user_type}</p>
            </div>
          ) : (
            <p>Failed to load user details.</p>
          )}

          <h3>Enter Current Password to Edit Details</h3>
          <form onSubmit={handlePasswordSubmit}>
            <div className="form-group">
              <label>Current Password</label>
              <input
                type="password"
                name="currentPassword"
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                className="form-control"
                required
              />
            </div>
            <button type="submit" className="btn btn-primary">Verify Password</button>
          </form>

          {error && <p style={{ color: 'red' }}>{error}</p>}
          {showUpdateForm && (
            <div>
              <h3>Edit Your Details</h3>
              {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label>Username</label>
                  <input
                    type="text"
                    name="username"
                    value={editForm.username}
                    onChange={handleChange}
                    className="form-control"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Email</label>
                  <input
                    type="email"
                    name="email"
                    value={editForm.email}
                    onChange={handleChange}
                    className="form-control"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Location</label>
                  <input
                    type="text"
                    name="location"
                    value={editForm.location}
                    onChange={handleChange}
                    className="form-control"
                  />
                </div>
                <div className="form-group">
                  <label>Password</label>
                  <input
                    type="password"
                    name="password"
                    value={editForm.password}
                    onChange={handleChange}
                    className="form-control"
                  />
                </div>
                <div className="form-group">
                  <label>Confirm Password</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={editForm.confirmPassword}
                    onChange={handleChange}
                    className="form-control"
                  />
                </div>
                <button type="submit" className="btn btn-primary">Update Details</button>
              </form>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Settings;
