import React, { useState, useEffect } from "react";
import axios from "axios";
import Sidebar from "../reuse/user-side";
import { Link } from "react-router-dom";

const Settings = () => {
  const [userdetails, setUserDetails] = useState('');
  
    useEffect(() => {
      // Fetch the logged-in user's details
      const token = localStorage.getItem('authToken');  // Get the token from localStorage
      console.log("Token being sent:", token);
  
      
      if (!token) {
        console.error("No token found, user is not logged in.");
        return;
      }
  
      // Fetch the user data from the backend
      fetch('http://127.0.0.1:8000/api/settings/', {
        method: 'GET',
        headers: {
          'Authorization': `Token ${token}`,  // Attach the token for authentication
          'Content-Type': 'application/json',
        },
      })
        .then(response => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          setUserDetails(data.userdetails);  // Set the username from the response data
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    }, []);

  return (
    <div>
      <div className='head-customer'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">SMARTPLAY</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto"></ul>
            </div>
          </div>
        </nav>
      </div>
      <div className='dashboard-container'>
        <Sidebar />
        <div className='dashboard-content'>
          <h1>Settings</h1>
          {userdetails ? (
            <div>
              <p><strong>Username:</strong> {userdetails.username}</p>
              <p><strong>Email:</strong> {userdetails.email}</p>
              <p><strong>Location:</strong> {userdetails.location}</p>
              <p><strong>User Type:</strong> {userdetails.user_type}</p>
            </div>
          ) : (
            <p>Loading user details...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Settings;
