// src/components/Login.js
import Header from "./reuse/Header";
import Footer from "./reuse/Footer";
import React, { useState } from "react";
import axios from "axios";
import Select from "react-select";

const Register = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    user_type: "",
    location: "",  // You can integrate with Google Maps API here
    password: "",
    password2: "",
  });

  const [message, setMessage] = useState("");

  const userTypeOptions = [
    { value: "Owner", label: "Owner" },
    { value: "Customer", label: "Customer" },
  ];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleUserTypeChange = (option) => {
    setFormData({ ...formData, user_type: option.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://127.0.0.1:8000/register/", formData);
      setMessage(response.data.message);
    } catch (error) {
      setMessage("Error: " + (error.response?.data || "Something went wrong"));
    }
  };


  return (
    <div>
      <Header />
    <div className="container mt-5">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Username</label>
          <input type="text" name="username" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label className="form-label">Email</label>
          <input type="email" name="email" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label className="form-label">User Type</label>
          <Select
            options={userTypeOptions}
            onChange={handleUserTypeChange}
            placeholder="Select user type"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Location</label>
          <input type="text" name="location" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label className="form-label">Password</label>
          <input type="password" name="password" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label className="form-label">Confirm Password</label>
          <input type="password" name="password2" className="form-control" onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-primary">Register</button>
      </form>
      {message && <p className="mt-3">{message}</p>}
    </div>
    <Footer />
    </div>
  );
};

export default Register;
