import React, { useState, useEffect } from "react";
import axios from "axios";
import Sidebar from "../reuse/user-side";
import { Link } from "react-router-dom";

const Settings = () => {
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    const fetchUserDetails = async () => {
        try {
          const token = localStorage.getItem("authToken"); // Get stored token
          if (!token) {
            console.error("No auth token found");
            return;
          }
      
          const response = await axios.get("http://127.0.0.1:8000/api/settings/", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
      
          setUserDetails(response.data);
        } catch (error) {
          console.error("Error fetching user details:", error);
        }
      };
      

    fetchUserDetails();
  }, []);

  return (
    <div>
      <div className='head-customer'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">SMARTPLAY</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto"></ul>
            </div>
          </div>
        </nav>
      </div>
      <div className='dashboard-container'>
        <Sidebar />
        <div className='dashboard-content'>
          <h1>Settings</h1>
          {userDetails ? (
            <div>
              <p><strong>Username:</strong> {userDetails.username}</p>
              <p><strong>Email:</strong> {userDetails.email}</p>
              <p><strong>Location:</strong> {userDetails.location}</p>
              <p><strong>User Type:</strong> {userDetails.user_type}</p>
            </div>
          ) : (
            <p>Loading user details...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Settings;
