//userdashboard
import { Link } from 'react-router-dom'; 
import Sidebar from './reuse/user-side';
import '../styles/user-dash.css';
import React, { useState, useEffect } from 'react';

function CustomerDashboard() {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetch('/api/customer-dashboard', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
      },
    })
    .then(response => response.json())
    .then(data => {
      const token = localStorage.getItem('token');
      console.log('Token:', token);  // Check if the token is retrieved correctly

      setUserData(data);
    })
    .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div>
      <div className='head'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">SMARTPLAY</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  {/* Check if userData is available before accessing userData.username */}
                  {userData ? (
                    <Link className="nav-link" to="#">{userData.username}</Link>
                  ) : (
                    <Link className="nav-link" to="#">Loading...</Link>
                  )}
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
      
      <div className="dashboard-container">
        <Sidebar />
        <div className="dashboard-content">
          <h2>Customer Dashboard</h2>
          <h2>Customer Dashboard</h2>
          {/* Add customer-specific content here */}
        </div>
      </div>
    </div>
  );
}

export default CustomerDashboard;
