import React, { useState, useEffect } from "react";
import Sidebar from "../reuse/user-side";
import { Link } from "react-router-dom";

const Settings = () => {
  const [userdetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editForm, setEditForm] = useState({
    username: '',
    email: '',
    location: '',
    password: '',
  });

  useEffect(() => {
    // Fetch the logged-in user's details
    const token = localStorage.getItem('authToken');  // Get the token from localStorage

    if (!token) {
      console.error("No token found, user is not logged in.");
      return;
    }

    fetch('http://127.0.0.1:8000/api/settings/', {
      method: 'GET',
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'application/json',
      },
    })
      .then(response => response.json())
      .then(data => {
        setUserDetails(data);  // Set the user data
        setEditForm({
          username: data.username,
          email: data.email,
          location: data.location || '',
          password: '',  // Password is handled separately
        });
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching user data:', error);
        setLoading(false);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const token = localStorage.getItem('authToken');
    if (!token) return;

    fetch('http://127.0.0.1:8000/api/settings/', {
      method: 'PUT',  // Assuming you have PUT method to update user details
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username: editForm.username,
        email: editForm.email,
        location: editForm.location,
        password: editForm.password,
      }),
    })
      .then(response => response.json())
      .then(data => {
        setUserDetails(data);
        setEditForm({
          username: data.username,
          email: data.email,
          location: data.location || '',
          password: '',
        });
      })
      .catch(error => console.error('Error updating user data:', error));
  };

  return (
    <div>
      <div className='head-customer'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">SMARTPLAY</Link>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto"></ul>
            </div>
          </div>
        </nav>
      </div>
      <div className='dashboard-container'>
        <Sidebar />
        <div className='dashboard-content'>
          <h1>Settings</h1>
          <h3>Your Details</h3>
          {loading ? (
            <p>Loading user details...</p>
          ) : userdetails ? (
            <div>
              <p><strong>Username:</strong> {userdetails.username}</p>
              <p><strong>Email:</strong> {userdetails.email}</p>
              <p><strong>Location:</strong> {userdetails.location || "Not provided"}</p>
              <p><strong>User Type:</strong> {userdetails.user_type}</p>
            </div>
          ) : (
            <p>Failed to load user details.</p>
          )}

          <h3>Edit Your Details</h3>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={editForm.username}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editForm.email}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="form-group">
              <label>Location</label>
              <input
                type="text"
                name="location"
                value={editForm.location}
                onChange={handleChange}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={editForm.password}
                onChange={handleChange}
                className="form-control"
              />
            </div>
            <button type="submit" className="btn btn-primary">Update Details</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Settings;
