import { useEffect, useState } from "react";

function Dashboard() {
    const [username, setUsername] = useState("");

    useEffect(() => {
        const token = localStorage.getItem("authToken"); // Retrieve token from localStorage
        if (!token) {
            console.error("No auth token found");
            return;
        }

        fetch("http://127.0.0.1:8000/api/customer-dashboard/", {
            method: "GET",
            headers: {
                "Authorization": `Token ${token}`,  // Attach the token for authentication
                "Content-Type": "application/json"
            }
        })
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
        })
        .then(data => {
            setUsername(data.username); // Set the username from API response
        })
        .catch(error => {
            console.error("Error fetching data:", error);
        });
    }, []);

    return (
        <div>
            <h1>Welcome, {username ? username : "Loading..."}</h1>
        </div>
    );
}

export default Dashboard;
