// src/components/Login.js

import Header from "./reuse/Header";
import Footer from "./reuse/Footer";
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';  // Import useHistory for navigation
import axios from 'axios';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  
  const history = useHistory();  // To handle navigation

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send POST request to backend with email and password
      const response = await axios.post('http://127.0.0.1:8000/api/login/', {
        email: email,
        password: password,
      });

      if (response.data.message === 'Login successful') {
        const userType = response.data.user_type;  // Get user type from backend response

        // If the user type is 'owner', navigate to the Owner Dashboard
        if (userType === 'owner') {
          history.push('/owner-dashboard');
        } else {
          // If the user type is 'customer', navigate to the Customer Dashboard
          history.push('/customer-dashboard');
        }
      }
    } catch (error) {
      setError('Invalid email or password');
    }
  };

  return (
    <div>
    <Header />
    <div className="login-container">
      <h2>Login</h2>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
    <Footer />
    </div>
  );
}

export default Login;
