import { Link } from 'react-router-dom';
import Sidebar from './reuse/user-side';
import '../styles/user-dash.css';
import React, { useState, useEffect } from 'react';

function CustomerDashboard() {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Get the token from localStorage
    const token = localStorage.getItem('authToken');
    const token2 = localStorage.getItem('authToken');

    console.log("Token from localStorage:", token2);
    if (token) {
      // Fetch user data from API
      fetch('/api/customer-dashboard/', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      })
        .then(response => response.text())  // Read the response as text
        .then(data => {
          console.log("Response Data:", data); // Log the response text
          try {
            const jsonData = JSON.parse(data);  // Manually parse the JSON
            setUserData(jsonData);
          } catch (error) {
            console.error('Error parsing JSON:', error);
          }
        })
        .catch((error) => console.error('Error fetching data:', error));
          }
    else
    {
      console.log("no token")
    }
  }, []);

  return (
    <div>
      <div className="head">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">
              SMARTPLAY
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  {userData ? (
                    <Link className="nav-link" to="">
                      {userData.username} {/* Display the username */}
                    </Link>
                  ) : (
                    <Link className="nav-link" to="">
                      Loading...
                    </Link>
                  )}
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
      <div className="dashboard-container">
        <Sidebar />
        <div className="dashboard-content">
          <h2>Customer Dashboard</h2>
          <h2>Customer Dashboard</h2>
          <h2>Customer Dashboard</h2>
          {/* Customer-specific content */}
        </div>
      </div>
    </div>
  );
}

export default CustomerDashboard;
