// src/Home.js
import React, { useEffect, useState } from 'react';

const Home = () => {
    const [message, setMessage] = useState('');

    // Fetch data from Django backend (Test connection)
    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/test/', { // API endpoint for testing
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        })
        .then(response => response.json())
        .then(data => {
            setMessage(data.message); // Set message from backend
        })
        .catch(error => {
            console.error('Error fetching data:', error);
            setMessage('Failed to connect to the backend.');
        });
    }, []);

    return (
        <div>
            <div className="flex flex-col min-h-screen">
            {/* Navbar */}
            <header className="bg-blue-600 text-white p-4 shadow-md">
                <div className="container mx-auto flex justify-between items-center">
                    <h1 className="text-2xl font-bold">MyWebsite</h1>
                    <nav>
                        <ul className="flex space-x-4">
                            <li><a href="/" className="hover:underline">Home</a></li>
                            <li><a href="/about" className="hover:underline">About</a></li>
                            <li><a href="/contact" className="hover:underline">Contact</a></li>
                        </ul>
                    </nav>
                </div>
            </header>

            {/* Main Content */}
            <main className="flex-grow flex flex-col items-center justify-center text-center p-6">
                <h2 className="text-4xl font-semibold mb-4">Welcome to My Website</h2>
                <p className="text-lg text-gray-700">{message}</p>
            </main>

            {/* Footer */}
            <footer className="bg-gray-800 text-white text-center p-4">
                <p>&copy; {new Date().getFullYear()} MyWebsite. All rights reserved.</p>
            </footer>
        </div>
        </div>
    );
};

export default Home;
