import { Link } from 'react-router-dom';
import Sidebar from './reuse/user-side';
import '../styles/user-dash.css';
import React, { useState, useEffect } from 'react';

function CustomerDashboard() {
  const [userData] = useState(null);

  useEffect(() => {
    // Get the token from localStorage

    const token = localStorage.getItem("authToken");
console.log("Token from localStorage:", token);  // Log the token for debugging

if (!token) {
  console.error("No token found.");
} else {
  fetch("http://127.0.0.1:8000/api/customer-dashboard/", {
    method: "GET",
    headers: {
      "Authorization": `Token ${token}`, // Ensure "Token" (not "Bearer" for Django Tokens)
      "Content-Type": "application/json"
    }
  })
  .then(response => {
    console.log("Response Status:", response.status);
    return response.json();
  })
    .then(data => {
      console.log("Received data:", data);
    })
    .catch(error => {
      console.error("Error fetching data:", error);
    });
}

  }, []);

  return (
    <div>
      <div className="head">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container-fluid">
            <Link className="navbar-brand" to="/">
              SMARTPLAY
            </Link>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  {userData ? (
                    <Link className="nav-link" to="">
                      {userData.username} {/* Display the username */}
                    </Link>
                  ) : (
                    <Link className="nav-link" to="">
                      Loading...
                    </Link>
                  )}
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
      <div className="dashboard-container">
        <Sidebar />
        <div className="dashboard-content">
          <h2>Customer Dashboard</h2>
          <h2>Customer Dashboard</h2>
          <h2>Customer Dashboard</h2>
          {/* Customer-specific content */}
        </div>
      </div>
    </div>
  );
}

export default CustomerDashboard;
