import React from "react";
import Button from '../ui/button'; // Adjust the path according to the actual file structure
import { Menu } from "lucide-react"; // Importing icons from Lucide
import { cn } from "clsx"; // Helps with conditional classes

const Header = () => {
  return (
    <header className="bg-gray-900 text-white shadow-lg sticky top-0 z-50">
      <nav className="max-w-7xl mx-auto flex items-center justify-between p-4">
        {/* Logo */}
        <h1 className="text-2xl font-bold text-white">My Website</h1>

        {/* Desktop Menu */}
        <ul className="hidden md:flex space-x-6 text-lg">
          <li><a href="/" className="hover:text-blue-400 transition duration-300">Home</a></li>
          <li><a href="/about" className="hover:text-blue-400 transition duration-300">About</a></li>
          <li><a href="/contact" className="hover:text-blue-400 transition duration-300">Contact</a></li>
        </ul>

        {/* Right Section (Login/Sign-Up) */}
        <div className="hidden md:flex space-x-3">
          <Button asChild>
            <a href="/Login">Login</a>
          </Button>
          <Button asChild className="bg-blue-500 hover:bg-blue-600">
            <a href="/Register">Sign Up</a>
          </Button>
        </div>

        {/* Mobile Menu Icon */}
        <button className="md:hidden">
          <Menu className="w-6 h-6" />
        </button>
      </nav>
    </header>
  );
};

export default Header;
