import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaCog, FaComments, FaSignOutAlt } from "react-icons/fa";

const Sidebar = () => {
  const navigate = useNavigate();

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem("authToken"); // Remove auth token
    navigate("/login"); // Redirect to login page
  };

  return (
    <div className="h-screen w-64 bg-gray-800 text-white fixed left-0 top-0 flex flex-col p-4 shadow-lg">
      <h2 className="text-xl font-bold mb-6">Dashboard</h2>

      <ul className="flex flex-col space-y-4">
        <li>
          <Link to="/settings" className="flex items-center space-x-2 hover:bg-gray-700 p-2 rounded">
            <FaCog /> <span>Settings</span>
          </Link>
        </li>
        <li>
          <Link to="/feedback" className="flex items-center space-x-2 hover:bg-gray-700 p-2 rounded">
            <FaComments /> <span>Feedback</span>
          </Link>
        </li>
        <li>
          <button
            onClick={handleLogout}
            className="flex items-center space-x-2 bg-red-500 px-4 py-2 rounded hover:bg-red-600"
          >
            <FaSignOutAlt /> <span>Logout</span>
          </button>
        </li>
      </ul>
    </div>
  );
};

export default Sidebar;
