import React, { useEffect, useState } from 'react';
import axios from 'axios';

const WeatherData = () => {
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        console.log("WeatherData component is rendering...");  // Debugging Log

        const fetchWeather = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/weather/');
                console.log("Weather data received in WeatherData:", response.data);
                setWeather(response.data);
            } catch (err) {
                setError('Error fetching weather data.');
                console.error('WeatherData API error:', err.response || err.message || err);
            }
        };

        fetchWeather();
    }, []);

    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    if (!weather) return <p>Loading weather data from WeatherData component...</p>;

    return (
        <div>
            <h2>Weather from WeatherData Component</h2>
            <p><strong>Temperature:</strong> {weather.main.temp}Â°C</p>
        </div>
    );
};

export default WeatherData;
