import React, { useState, useEffect } from "react";
import axios from "axios";
import Sidebar from "../reuse/user-side";
import { Link } from "react-router-dom";

const Settings = () => {
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/api/settings/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("authToken")}`, // Ensure token is stored in localStorage
          },
        });
        setUserDetails(response.data);
      } catch (error) {
        console.error("Error fetching user details:", error);
      }
    };

    fetchUserDetails();
  }, []);

  return (
    <div>
    <div className='head-customer'>
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">
            <Link className="navbar-brand" to="/">SMARTPLAY</Link>  {/* Use Link for navigation */}
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
                <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                    
                </li>
                <li className="nav-item">
                
                </li>
                </ul>
            </div>
            </div>
        </nav>
    </div>
    <div className='dashboard-container'>
    
        <Sidebar/>

    <h1>Settings</h1>
    <h1>Settings</h1>
      <p>Username: {userDetails.username}</p>
      <p>Email: {userDetails.email}</p>
      <p>User Type: {userDetails.user_type}</p>
      <p>Location: {userDetails.location}</p>
    </div>
    </div>
  );
};

export default Settings;
