import React, { useEffect, useState } from 'react';
import Sidebar from './reuse/user-side';
import '../styles/user-dash.css';  
import '../styles/head-common.css'; 
import { Link } from 'react-router-dom'; 
import axios from 'axios';

function CustomerDashboard() {
  const [username, setUsername] = useState('');
  const [weather, setWeather] = useState(null);
  const [pastWeather, setPastWeather] = useState([]);
  const [futureWeather, setFutureWeather] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('authToken');

    if (!token) {
      console.error("No token found, user is not logged in.");
      return;
    }

    // Fetch user data
    fetch('http://127.0.0.1:8000/api/user-details/', {
      method: 'GET',
      headers: {
        'Authorization': `Token ${token}`,
        'Content-Type': 'application/json',
      },
    })
      .then(response => response.json())
      .then(data => setUsername(data.username))
      .catch(error => console.error('Error fetching user data:', error));

    // Fetch weather data
    const fetchWeather = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/weather/', {
          headers: {
            'Authorization': `Token ${token}`,
          },
        });

        setWeather(response.data.current);
        setFutureWeather(response.data.forecast.list);
        setPastWeather(response.data.past);
      } catch (err) {
        setError('Error fetching weather data.');
        console.error('Error:', err.response || err.message || err);
      }
    };

    fetchWeather();
  }, []);

  return (
    <div>
        <div className='head-customer'>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container-fluid">
                <Link className="navbar-brand" to="/">SMARTPLAY</Link>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ms-auto">
                    <li className="nav-item">
                        <Link className="nav-link active" to="">{username ? username : 'Loading...'}</Link>
                    </li>
                    </ul>
                </div>
                </div>
            </nav>
        </div>
        <div className='dashboard-container'>
            <Sidebar/>
            <div className='dashboard-content'>
            <h2>Customer Dashboard</h2>
            <p>Welcome, {username ? username : 'Loading...'}!</p>

            <div>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {weather ? (
                  <div>
                      <h2>{weather.name}, {weather.sys.country}</h2>
                      <p><strong>Temperature:</strong> {weather.main.temp}°C</p>
                      <p><strong>Weather:</strong> {weather.weather[0].description}</p>
                      <img src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`} alt="weather-icon" />
                  </div>
                ) : <p>Loading weather data...</p>}

                <h3>Past 7 Days Weather</h3>
                {pastWeather.length > 0 ? pastWeather.map((day, index) => (
                  <div key={index}>
                    <p><strong>Date:</strong> {new Date(day.current.dt * 1000).toLocaleDateString()}</p>
                    <p><strong>Temperature:</strong> {day.current.temp}°C</p>
                    <p><strong>Weather:</strong> {day.current.weather[0].description}</p>
                  </div>
                )) : <p>Loading past weather...</p>}

                <h3>Next 7 Days Forecast</h3>
                {futureWeather.length > 0 ? futureWeather.map((forecast, index) => (
                  <div key={index}>
                    <p><strong>Date:</strong> {new Date(forecast.dt * 1000).toLocaleDateString()}</p>
                    <p><strong>Temperature:</strong> {forecast.main.temp}°C</p>
                    <p><strong>Weather:</strong> {forecast.weather[0].description}</p>
                  </div>
                )) : <p>Loading future weather...</p>}
            </div>
            </div>
        </div>
    </div>
  );
}

export default CustomerDashboard;
