// CustomerDashboard.js
import React from 'react';
import { Link } from 'react-router-dom'; 
import Sidebar from './reuse/user-side';
import '../styles/user-dash.css';


function CustomerDashboard() {

  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Fetch the user data from the backend
    fetch('/api/dashboard/', {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Assuming you're using JWT token for authentication
      }
    })
      .then(response => response.json())
      .then(data => setUserData(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <div>
      <div className='head'>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">SMARTPLAY</Link>  {/* Use Link for navigation */}
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
              {userData ? (<Link className="nav-link" to="/register">{userData.username}</Link>):(<p>Loading...</p>)}
              </li>
            </ul>
          </div>
        </div>
      </nav>
      </div>
    <div className="dashboard-container">
      <Sidebar />
      
      <div className="dashboard-content">
        <h2>Customer Dashboard</h2>
        <h2>Customer Dashboard</h2><h2>Customer Dashboard</h2>
        
        {/* Customer-specific content */}
      </div>
    </div>
    </div>
  );
}

export default CustomerDashboard;
